public class GeneCount {
    int getCount(String dna){
        int count=0;
        int start,stop,cur=0;
        start = dna.indexOf("ATG",cur);
        stop = dna.indexOf("TAA");
        while(true) {
//            start = dna.indexOf("ATG",cur);
            if (start == -1) break;
//            stop = dna.indexOf("TAA");
            if (stop == -1) break;
            if (start <dna.length() && stop+3<dna.length() && dna.substring(start, stop + 3).length() % 3 == 0){
                count++;
                System.out.println(dna.substring(start, stop + 3));
                cur=stop+3;
                start = dna.indexOf("ATG",cur);
            }
            else if (start <dna.length() && stop+3<dna.length() && dna.substring(start, stop + 3).length() % 3 != 0){
                cur=stop+3;
            }
            stop = dna.indexOf("TAA",cur);
        }
        return count;
    }

    public static void main(String[] args) {
        GeneCount g = new GeneCount();
        System.out.println(g.getCount(""));
    }
}
